			child = this._addItem(item.id,null,null,list[i]);
			if (child == null) continue;
			
			label = child.base.childNodes[1];
			if (label && !label.innerHTML && !/\s?dhxrb_label_hide/i.test(label.className)) {
				label.className += " dhxrb_label_hide";
			}
