					if(config.line&&config.line.shadow){
						ctx.globalAlpha = 0.3;

						this._drawLine(ctx,x1+2,y1+config.line.width+8,x2+2,y2+config.line.width+8,"#eeeeee",config.line.width+3);
						ctx.globalAlpha = 1;
					}
